  width: 350px;
  height: 100vh;
  left: 0px;
  z-index: 1000;
  display: block;
  background-color: #000000;
  text-align: center;

  .positioning {
    position: relative;
    top: 50%;
    transform: translateY(-50%);
  }

  .img-size {
    height: auto;
    max-width: 75%;
    border-radius: 50%;
  }  
}
 
ul {
  text-decoration: none;
  list-style: none;
}

nav li a {
  text-decoration: none;
  font-weight: 800;
  font-size: 1.1em;
  line-height: 2.5em;
  color: #dddbdb;
}

nav li a.active {
  color: #067cca;
}

nav li a:hover {
  color: #067cca;
}

/* ================================================================================================================= */

/* CONTACT ANIMATION*/
.contact-anim{
  position: relative;
  text-decoration: none;
  transition: color .4s ease-out;

  &:after {
    border-radius: 1em;
    border-top: .08em solid #067cca;
    content: "";
    position: absolute;
      right: 100%;
      bottom: .14em;
      left: 0;
    transition: right .4s cubic-bezier(0,.5,0,1),
                border-color .4s ease-out;
  }
  
  &:after {
    animation: anchor-underline 2s cubic-bezier(0,.5,0,1) infinite;
    border-color: #067cca;
  }
}

@keyframes anchor-underline {
  0%, 10% {
    left: 0;
    right: 100%;
  }
  40%, 60% {
    left: 0;
    right: 0;
  }
  90%, 100% {
    left: 100%;
    right: 0;
  }
}

.build-span:before {
  content: "Design";
  animation: build infinite 3s;
}

@keyframes build {
  0% {
    content: "Create";
  }
  50% {
    content: "Build";
  }
  100% {
    content: "Design";
  }
}

.change-span:before {
  content: "Experiences";
  animation: change infinite 3s;
}

@keyframes change {
  0% {
    content: "Collaborations";
  }
  50% {
    content: "New Projects";
  }
  100% {
    content: "Experiences";
  }
}

