  width: 640px;
  height: 385px;
  margin-top: 80px;
  margin-left: 52px;
}

.overlay {
  position: relative;
  width: 750px;
  height: 500px;
  object-fit: cover;
  margin: auto;
  img {
    position: absolute;
    width: 750px;
    height: 600px;
    pointer-events: none;
  }
}

.overlay-phone {
  position: relative;
  width: 760px;
  height: 420px;
  object-fit: cover;
  margin: auto;
  img {
    position: absolute;
    width: 760px;
    height: 420px;
    margin-top: 55px;
    pointer-events: none;
  }
}

.overlay-pics {
  position: relative;
  width: 100%;
  height: 500px;
  object-fit: cover;
  margin: auto;
  img {
    position: absolute;
    object-fit: cover;
    width: 760px;
    top: 5.5%;
    object-fit: cover;
  }
}

.git-img-phone {
  max-height: 80px;
  max-width: 300px;
  margin: 60px auto 0 auto;
}

