/* RESPONSIVE NAV */
@media screen and (max-width: 800px) {
    
    /* MOB NAVIGATION */
    .hamburger{
      top: 2.5%;
      right: 5%;
      width: 50px;
      height: 15px;
      position: fixed;
      -webkit-transform: rotate(0deg);
      -moz-transform: rotate(0deg);
      -o-transform: rotate(0deg);
      transform: rotate(0deg);
      -webkit-transition: .5s ease-in-out;
      -moz-transition: .5s ease-in-out;
      -o-transition: .5s ease-in-out;
      transition: .5s ease-in-out;
      cursor: pointer;
      z-index: 999;
    }
    
    .hamburger span {
      display: block;
      position: absolute;
      height: 5px;
      width: 100%;
      background: #067cca;
      border-radius: 9px;
      opacity: 1;
      left: 0;
      -webkit-transform: rotate(0deg);
      -moz-transform: rotate(0deg);
      -o-transform: rotate(0deg);
      transform: rotate(0deg);
      -webkit-transition: .25s ease-in-out;
      -moz-transition: .25s ease-in-out;
      -o-transition: .25s ease-in-out;
      transition: .25s ease-in-out;
    }
    
    .hamburger span:nth-child(1) {
      top: 0px;
    }
    
    .hamburger span:nth-child(2) {
      top: 13px;
    }
    
    .hamburger span:nth-child(3) {
      top: 26px;
    }
    
    .hamburger.open span:nth-child(1) {
      top: 18px;
      -webkit-transform: rotate(135deg);
      -moz-transform: rotate(135deg);
      -o-transform: rotate(135deg);
      transform: rotate(135deg);
    }
    
    .hamburger.open span:nth-child(2) {
      opacity: 0;
      left: -60px;
    }
    
    .hamburger.open span:nth-child(3) {
      top: 18px;
      -webkit-transform: rotate(-135deg);
      -moz-transform: rotate(-135deg);
      -o-transform: rotate(-135deg);
      transform: rotate(-135deg);
    }
  
    .sidebar {
      position: fixed;
      background: #000000;
      height: 100vh;
      width: 100%;
      flex-direction: column;
      clip-path: circle(100px at 95% 0%);
      -webkit-clip-path: circle(90px at 92% -5%);
      transition: all 0.6s ease-out;
      pointer-events: none;
      z-index: 98;
    }
    .sidebar img {
      display: none;
    }
  
    .sidebar.open {
      clip-path: circle(1500px at 100% -10%);
      -webkit-clip-path: circle(1500px at 100% -10%);
      pointer-events: all;
    }
  
    .sidebar li {
      opacity: 0;
    }
  
    .sidebar li a {
      font-size: 20px;
      line-height: 5em;
    }
  
    .sidebar li:nth-child(1) {
      transition: all 0.5s ease 0.2s;
    }
  
    .sidebar li:nth-child(2) {
      transition: all 0.5s ease 0.4s;
    }
  
    .sidebar li:nth-child(3) {
      transition: all 0.5s ease 0.6s;
    }
  
    .sidebar li:nth-child(4) {
      transition: all 0.5s ease 0.8s;
    }
  
    .sidebar li:nth-child(5) {
      transition: all 0.5s ease 1s;
    }
  
    li.fade {
      opacity: 1;
    }
  
    /* ================================================================================================================= */
  
    /* MOB CONTAINER */
    .container {
      height: 100vh;
      max-width: 100%;
      margin-right: auto;
      margin-left: 20px;
      display: inline-block;
    }
  
    .container-positioning  {
      height: 100%;
      width: 100%;
    }

    .container-positioning  .paragraph-positioning {
      position: relative;
      top: 50%;
      transform: translateY(-50%);
    }
  
    .section-positioning {
      height: 100%;
      width: 100%;
    }
    .section-positioning .paragraph-positioning {
      position: relative;
      top: 0;
      transform: translateY(0);
    }
  
    .wrapper {
      width: 100%;
      height: auto;
    }
    .wrapper img {
      width: 100%;
      height: auto;
    }
  
    .cube-img {
      display: none;
    }
    .cube-img img {
      display: none;
    }
  
    .overlay {
      display: none;
    }
  
    .overlay-phone {
      display: none;
    }
  
    .overlay-pics {
      display: none;
    }
  
    .vid-display {
      display: block;
    }
  
    h3 {
      font-size: 1.5rem;
    }
  
    .contact-font {
      font-size: 0.5rem;
      color: #333333;
    }
    
    /* ================================================================================================================= */
  
    /* MOB SOCIAL BUTTON */
    .social-buttons {
        font-size: 2vw;
        display: flex;
      }
    
    .social-icon {
        display: inline-block;
        border-radius: 100%;
        padding: 15px;
        margin: 10px;
    }

    /* PRE-LOADER */
    .loader{
      transform: scale(1.5);
    }
  
    .inner-loader .loader-title {
      font-size: 20px;
      font-weight: 400;
    }
}  

